import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0" +
  " hover-elevate active-elevate-2",
  {
    variants: {
      variant: {
        default:
          "bg-primary text-primary-foreground border border-primary-border",
        destructive:
          "bg-destructive text-destructive-foreground border border-destructive-border",
        outline:
          // Shows the background color of whatever card / sidebar / accent background it is inside of.
          // Inherits the current text color.
          " border [border-color:var(--button-outline)]  shadow-xs active:shadow-none ",
        secondary: "border bg-secondary text-secondary-foreground border border-secondary-border ",
        // Add a transparent border so that when someone toggles a border on later, it doesn't shift layout/size.
        ghost: "border border-transparent",
      },
      // Heights are set as "min" heights, because sometimes Ai will place large amount of content
      // inside buttons. With a min-height they will look appropriate with small amounts of content,
      // but will expand to fit large amounts of content.
      size: {
        default: "min-h-9 px-4 py-2",
        sm: "min-h-8 rounded-md px-3 text-xs",
        lg: "min-h-10 rounded-md px-8",
        icon: "h-9 w-9",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
)

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button"
    return (
      <Comp
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      />
    )
  },
)
Button.displayName = "Button"

export { Button, buttonVariants }

client/src/components/PostDetail.tsx.content:
import { ArrowLeft, ArrowUp, User, MessageSquare } from "lucide-react";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Checkbox } from "@/components/ui/checkbox";
import { Label } from "@/components/ui/label";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { CommentThread } from "./CommentThread";
import { useState } from "react";

type PostStatus = "pending" | "reviewing" | "in_progress" | "completed" | "wont_fix" | null;

interface PostDetailProps {
  post: {
    id: string;
    title: string;
    description: string;
    author?: string;
    isAnonymous: boolean;
    timestamp: string;
    tags: string[];
    upvotes: number;
    status?: PostStatus;
    hasUpvoted?: boolean;
    imageUrl?: string;
  };
  comments: any[];
  isAdmin?: boolean;
  canEdit?: boolean;
  onBack?: () => void;
  onUpvote?: () => void;
  onCommentUpvote?: (commentId: string) => void;
  onStatusChange?: (status: PostStatus) => void;
  onAddComment?: (content: string, isAnonymous: boolean) => void;
  onEdit?: () => void;
  onDelete?: () => void;
}

const statusColors: Record<string, string> = {
  pending: "bg-muted text-muted-foreground",
  reviewing: "bg-muted text-muted-foreground",
  in_progress: "bg-primary/10 text-primary border-primary/20",
  completed: "bg-green-500/10 text-green-600 dark:text-green-400 border-green-500/20",
  wont_fix: "bg-destructive/10 text-destructive border-destructive/20",
};

const statusLabels: Record<string, string> = {
  pending: "Pending",
  reviewing: "Under Review",
  in_progress: "In Progress",
  completed: "Completed",
  wont_fix: "Won't Fix",
};

export function PostDetail({
  post,
  comments,
  isAdmin = false,
  canEdit = false,
  onBack,
  onUpvote,
  onCommentUpvote,
  onStatusChange,
  onAddComment,
  onEdit,
  onDelete,
}: PostDetailProps) {
  const [localUpvoted, setLocalUpvoted] = useState(post.hasUpvoted || false);
  const [localUpvotes, setLocalUpvotes] = useState(post.upvotes);
  const [commentContent, setCommentContent] = useState("");
  const [commentAnonymous, setCommentAnonymous] = useState(false);

  const handleUpvote = () => {
    setLocalUpvoted(!localUpvoted);
    setLocalUpvotes(localUpvoted ? localUpvotes - 1 : localUpvotes + 1);
    onUpvote?.();
  };

  const handleStatusChange = (newStatus: PostStatus) => {
    console.log(`Status changed to: ${newStatus}`);
    onStatusChange?.(newStatus);
  };

  const handleAddComment = () => {
    if (commentContent.trim()) {
      console.log("Adding comment:", { content: commentContent, isAnonymous: commentAnonymous });
      onAddComment?.(commentContent, commentAnonymous);
      setCommentContent("");
      setCommentAnonymous(false);
    }
  };

  return (
    <div className="min-h-screen bg-background">
      <div className="max-w-4xl mx-auto">
        <div className="sticky top-0 z-10 bg-background border-b px-4 py-3">
          <Button
            variant="ghost"
            onClick={onBack}
            data-testid="button-back"
          >
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back to Feed
          </Button>
        </div>

        <div className="p-6">
          <div className="flex gap-6">
            <div className="flex flex-col items-center gap-2 w-20 shrink-0">
              <Button
                size="icon"
                variant={localUpvoted ? "default" : "outline"}
                onClick={handleUpvote}
                data-testid="button-upvote"
              >
                <ArrowUp className="h-5 w-5" />
              </Button>
              <span className="text-lg font-bold" data-testid="text-upvote-count">
                {localUpvotes}
              </span>
              <span className="text-xs text-muted-foreground">votes</span>
            </div>

            <div className="flex-1 min-w-0 space-y-6">
              <div className="space-y-4">
                <div className="flex items-start justify-between gap-4 flex-wrap">
                  <div>
                    <div className="flex items-center gap-2 text-sm text-muted-foreground mb-2">
                      {post.isAnonymous ? (
                        <>
                          <User className="h-4 w-4" />
                          <span>Anonymous</span>
                        </>
                      ) : (
                        <span>{post.author}</span>
                      )}
                      <span></span>
                      <span>{post.timestamp}</span>
                    </div>
                    <h1 className="text-3xl font-bold mb-2" data-testid="text-post-title">
                      {post.title}
                    </h1>
                  </div>
                  {post.status && (
                    <Badge className={statusColors[post.status]} data-testid="badge-status">
                      {statusLabels[post.status]}
                    </Badge>
                  )}
                </div>

                <p className="text-base leading-relaxed">{post.description}</p>

                {post.imageUrl && (
                  <div className="rounded-lg overflow-hidden">
                    <img
                      src={post.imageUrl}
                      alt="Post attachment"
                      className="max-h-96 w-full object-cover"
                      data-testid="img-post-attachment"
                    />
                  </div>
                )}

                <div className="flex items-center gap-3 flex-wrap">
                  {post.tags.map((tag) => (
                    <Badge
                      key={tag}
                      variant="secondary"
                      className="rounded-full px-3 py-1"
                      data-testid={`badge-tag-${tag.toLowerCase()}`}
                    >
                      {tag}
                    </Badge>
                  ))}
                </div>

                <div className="flex items-center gap-2 pt-2">
                  {isAdmin && (
                    <DropdownMenu>
                      <DropdownMenuTrigger asChild>
                        <Button variant="outline" data-testid="button-status-dropdown">
                          Update Status
                        </Button>
                      </DropdownMenuTrigger>
                      <DropdownMenuContent>
                        <DropdownMenuItem onClick={() => handleStatusChange("pending")}>
                          Pending
                        </DropdownMenuItem>
                        <DropdownMenuItem onClick={() => handleStatusChange("reviewing")}>
                          Under Review
                        </DropdownMenuItem>
                        <DropdownMenuItem onClick={() => handleStatusChange("in_progress")}>
                          In Progress
                        </DropdownMenuItem>
                        <DropdownMenuItem onClick={() => handleStatusChange("completed")}>
                          Completed
                        </DropdownMenuItem>
                        <DropdownMenuItem onClick={() => handleStatusChange("wont_fix")}>
                          Won't Fix
                        </DropdownMenuItem>
                      </DropdownMenuContent>
                    </DropdownMenu>
                  )}
                  {canEdit && (
                    <>
                      <Button variant="outline" onClick={onEdit} data-testid="button-edit">
                        Edit
                      </Button>
                      <Button
                        variant="outline"
                        className="text-destructive hover:text-destructive"
                        onClick={onDelete}
                        data-testid="button-delete"
                      >
                        Delete
                      </Button>
                    </>
                  )}
                </div>
              </div>

              <div className="border-t pt-6 space-y-4">
                <h2 className="text-lg font-semibold flex items-center gap-2">
                  <MessageSquare className="h-5 w-5" />
                  Comments ({comments.length})
                </h2>

                <div className="space-y-3">
                  <Textarea
                    placeholder="Share your thoughts..."
                    value={commentContent}
                    onChange={(e) => setCommentContent(e.target.value)}
                    rows={3}
                    data-testid="input-comment"
                  />
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-2">
                      <Checkbox
                        id="comment-anonymous"
                        checked={commentAnonymous}
                        onCheckedChange={(checked) =>
                          setCommentAnonymous(checked as boolean)
                        }
                        data-testid="checkbox-anonymous"
                      />
                      <Label
                        htmlFor="comment-anonymous"
                        className="text-sm font-normal cursor-pointer"
                      >
                        Comment anonymously
                      </Label>
                    </div>
                    <Button
                      onClick={handleAddComment}
                      disabled={!commentContent.trim()}
                      data-testid="button-submit-comment"
                    >
                      Comment
                    </Button>
                  </div>
                </div>

                <div className="pt-4">
                  <CommentThread comments={comments} onUpvote={onCommentUpvote} />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/AuthPage.tsx.content:
import { useState } from "react";
import { Mail, ArrowRight, CheckCircle2, Sparkles } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";

interface AuthPageProps {
  onSendMagicLink?: (email: string) => void;
}

export function AuthPage({ onSendMagicLink }: AuthPageProps) {
  const [email, setEmail] = useState("");
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [error, setError] = useState("");

  const validateEmail = (email: string) => {
    const emailRegex = /^[^\s@]+@my\.fisk\.edu$/;
    return emailRegex.test(email);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setError("");

    if (!validateEmail(email)) {
      setError("Please use a valid @my.fisk.edu email address");
      return;
    }

    console.log("Sending magic link to:", email);
    onSendMagicLink?.(email);
    setIsSubmitted(true);
  };

  if (isSubmitted) {
    return (
      <div className="min-h-screen flex items-center justify-center p-6 bg-gradient-to-br from-primary/10 via-background to-secondary/10">
        <div className="w-full max-w-md backdrop-blur-xl bg-white/80 dark:bg-background/80 rounded-3xl shadow-2xl border border-white/20 overflow-hidden">
          <div className="p-10 md:p-12 text-center space-y-6">
            <div className="mx-auto h-20 w-20 rounded-full bg-gradient-to-br from-secondary to-secondary/80 flex items-center justify-center shadow-xl">
              <CheckCircle2 className="h-10 w-10 text-secondary-foreground" />
            </div>
            <div className="space-y-3">
              <h2 className="text-3xl font-bold tracking-tight">Check your email</h2>
              <p className="text-base text-muted-foreground">
                We've sent a magic link to <strong className="text-foreground">{email}</strong>
              </p>
            </div>
            <div className="pt-4 space-y-4">
              <p className="text-sm text-muted-foreground">
                Click the link in your email to sign in. The link will expire in 15
                minutes.
              </p>
              <Button
                variant="ghost"
                className="w-full rounded-full h-12"
                onClick={() => {
                  setIsSubmitted(false);
                  setEmail("");
                }}
                data-testid="button-back"
              >
                Use a different email
              </Button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex items-center justify-center p-6 bg-gradient-to-br from-primary/10 via-background to-secondary/10">
      <div className="w-full max-w-md backdrop-blur-xl bg-white/80 dark:bg-background/80 rounded-3xl shadow-2xl border border-white/20 overflow-hidden">
        <div className="p-10 md:p-12 text-center space-y-8">
          <div className="space-y-4">
            <div className="mx-auto h-16 w-16 md:h-20 md:w-20 rounded-2xl bg-gradient-to-br from-primary to-primary/80 flex items-center justify-center shadow-xl">
              <span className="text-primary-foreground font-bold text-3xl md:text-4xl">F</span>
            </div>
            <div className="space-y-2">
              <h1 className="text-3xl md:text-4xl font-bold tracking-tight">
                Welcome to Fisk Feedback
              </h1>
              <p className="text-base text-muted-foreground">
                Share your voice and help improve campus life
              </p>
            </div>
          </div>

          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="space-y-3 text-left">
              <Label htmlFor="email" className="text-base font-semibold">
                Fisk University Email
              </Label>
              <div className="relative">
                <Mail className="absolute left-4 top-1/2 -translate-y-1/2 h-5 w-5 text-muted-foreground" />
                <Input
                  id="email"
                  type="email"
                  placeholder="yourname@my.fisk.edu"
                  value={email}
                  onChange={(e) => {
                    setEmail(e.target.value);
                    setError("");
                  }}
                  className="pl-12 h-14 text-base rounded-xl"
                  data-testid="input-email"
                />
              </div>
              {error && (
                <p className="text-sm text-destructive font-medium" data-testid="text-error">
                  {error}
                </p>
              )}
              <p className="text-xs text-muted-foreground">
                Only @my.fisk.edu email addresses are allowed
              </p>
            </div>

            <Button
              type="submit"
              className="w-full h-14 text-lg rounded-full font-semibold shadow-lg hover:shadow-xl transition-all"
              disabled={!email}
              data-testid="button-send-magic-link"
            >
              Send Magic Link
              <ArrowRight className="h-5 w-5 ml-2" />
            </Button>
          </form>

          <div className="flex items-center gap-2 justify-center text-sm text-muted-foreground pt-4">
            <Sparkles className="h-4 w-4" />
            <p>
              We'll send you a secure login link
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

